import { Avatar, Text, AvatarGroup } from '@innovaccer/design-system';
    /*
// style.css
.UserAvatars-popover {
    max-height: var(--spacing-7);
}
*/

() => {
  const list = [
    {
      firstName: 'John',
      lastName: 'Doe',
    },
    {
      firstName: 'Steven',
      lastName: 'Packton',
    },
    {
      firstName: 'Nancy',
      lastName: 'Wheeler'
    },
    {
      firstName: 'Monica',
      lastName: 'Geller'
    },
    {
      firstName: 'Arya',
      lastName: 'Stark',
    },
    {
      firstName: 'Rachel',
      lastName: 'Green',
    },
    {
      firstName: 'Walter',
      lastName: 'Wheeler'
    },
  ];

  const popperRenderer = (list) => {
    const AvatarList = list.map((avatar, index) => {
      const { firstName, lastName, appearance } = avatar;

      return (
        <div className="d-flex align-items-center mr-4 mb-4" key={index}>
          <Avatar
            firstName={firstName}
            lastName={lastName}
            appearance={appearance}
            className="mr-4"
            withTooltip={false}
          />
          <Text>{`${firstName} ${lastName}`}</Text>
        </div>
      )
    });

    return (
      <div className="overflow-auto py-4 px-6 UserAvatars-popover">
        {AvatarList}
      </div>
    )
  };

  return (
    <AvatarGroup
      list={list}
      popoverOptions={{ dark: false, on: 'click', popperRenderer }}
    />
  );
}

backdrop.code:
import { Button, Backdrop } from '@innovaccer/design-system';
    () => {
  const [open, setOpen] = React.useState(false);

  const onKeyDown = (event) => {
    if (event.key === 'Escape') {
      setOpen(false);
    }
  };

  return (
    <div onKeyDown={onKeyDown}>
      <Button onClick={() => setOpen(true)} appearance="primary">
        Trigger Backdrop
      </Button>
      <div onClick={() => setOpen(false)}>
        <Backdrop open={open} />
      </div>
    </div>
  );
}

