              clearButton={false}
              type="selection"
              className={classNames}
              selected={selectedChip === 'thirtyDaysLater'}
              name="rangePicker"
              onClick={() => {
                setDate(getThirtyDaysLaterDate())
                setSelectedChip('thirtyDaysLater')
              }}
            />
          </div>
        </div>
        <Divider vertical={true} />
      </DatePicker>
    );
  };
  return (
    <>
      <Card className="d-inline-flex" shadow="light">
        <DatePickerPreset />
