        </Button>
        <Button appearance="basic">Basic</Button>
      </>
    ),
  };

  const SecondOverlayOptions = {
    dimension,
    seperator,
    headerOptions,
    stickFooter,
    backdropClose,
    onClose: onCloseSecond,
    open: openSecond,
    footer: (
