      }, 2000);
    });
  }

  const loaderSchema = [
    {
        "name": "name",
        "displayName": "Name",
        "width": "40%",
        "resizable": true,
        "tooltip": true,
        "separator": true,
        "filters": [
            {
                "label": "A-G",
                "value": "a-g"
            },
            {
                "label": "H-R",
                "value": "h-r"
            },
            {
                "label": "S-Z",
                "value": "s-z"
            }
        ],
        "cellType": "AVATAR_WITH_TEXT"
    },
    {
        "name": "email",
        "displayName": "Email",
        "width": 250,
        "resizable": true,
        "sorting": false,
        "cellType": "WITH_META_LIST"
    },
    {
        "name": "gender",
        "displayName": "Gender",
        "width": 180,
        "resizable": true,
        "cellType": "STATUS_HINT",
        "filters": [
            {
                "label": "Male",
                "value": "male"
            },
            {
                "label": "Female",
                "value": "female"
            }
        ]
    },
    {
        "name": "icon",
        "displayName": "Icon",
        "width": 100,
        "resizable": true,
        "align": "center",
        "cellType": "ICON"
    },
    {
        "name": "customCell",
        "displayName": "Custom Cell",
        "width": 200,
        "resizable": true,
        "cellType": "WITH_META_LIST"
    }
];

  return (
    <div>
      <Card className="h-100 overflow-hidden">
        <Table
          loaderSchema={loaderSchema}
          fetchData={fetchData}
          withHeader={true}
          headerOptions={{
            withSearch: true,
            allowSelectAll: true,
            customSelectionLabel: 'user',
          }}
          withCheckbox={true}
          onSelect={(rowIndex, selected, selectedList, selectAll) => console.log(`on-select: - rowIndex: ${ rowIndex } selected: ${ selected } selectedList: ${ JSON.stringify(selectedList) } selectAll: ${ selectAll } `)}
          withPagination={true}
          pageSize={5}
          onPageChange={newPage => console.log(`on-page-change:- ${newPage}`)}
        />
      </Card>
    </div>
  );
};

