    const { name, displayName, width, separator, cellType, cellRenderer } = s;
    return {
      name, displayName, width, separator, cellType, cellRenderer
    };
  });

  return (
    <div className="bg-secondary-lightest">
      <Table
        loaderSchema={loaderSchema}
        fetchData={fetchData}
      />
    </div>
